mapper.select:
SELECT	count(*)
FROM	member
WHERE	id = #{id}

SELECT COUNT(*)
FROM member
WHERE tel = #{tel}

SELECT	*
FROM	member
WHERE	id			= #{id}
 AND	password	= #{password}

SELECT COUNT(*)
FROM member
WHERE id = #{id}
AND tel = #{tel}

mapper.update:
UPDATE member
SET password = #{password}
WHERE id = #{id}

mapper.delete:
DELETE FROM member
WHERE	id = #{id}
AND	password = #{password}

mapper.update:
UPDATE member
SET password=#{password}, name=#{name}, tel=#{tel}, address=#{address}, birth=#{birth}
WHERE id=#{id}

mapper.select:
SELECT COUNT(*)
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member
SET m_cc = #{ccid}
WHERE id = #{id}

mapper.select:
select count(m_cc)
from member
where id=#{ccid}
